import React, {useState} from 'react';
import Error from './Error';
import shorthid from 'shortid';

const Formulario = ({guardarGasto}) => {
    const [nombre, guardarNombre] = useState('');
    const [cantidad, guardarCantidad] = useState(0);
    const [error, guardarError] = useState(false)

    //Cuando el usuario agrega gasto
    const agregarGasto = e=> {
        e.preventDefault();

        //validar
        if(cantidad < 1 || isNaN(cantidad) || nombre.trim() === '') {
            guardarError(true);
            return;
        }
        guardarError(false);

        //construir gasto
        const gasto = {
            nombre,
            cantidad,
            id: shorthid.generate()
        }

        //Pasar el gasto al componente principal
        guardarGasto(gasto);
        //reset form
        guardarNombre('');
        guardarCantidad(0);
    }
    return ( 
        <form onSubmit={agregarGasto}>
            <h2>Agrega tus datos aqui</h2>
            {error ? <Error mensaje="Ambos campos son obligatorios o presupuesto incompleto"/> : null}
            <div className="campo">
                <label>Nombre Gasto</label>
                <input 
                    type="text"
                    className="u-full-width"
                    placeholder="Ej. Transporte"
                    value={nombre}
                    onChange={e => guardarNombre(e.target.value)}
                />
            </div>

            <div className="campo">
                <label>Cantidad Gasto</label>
                <input 
                    type="number"
                    className="u-full-width"
                    placeholder="Ej. 300"
                    value={cantidad}
                    onChange={e =>guardarCantidad(parseInt(e.target.value, 10))}
                />
            </div>

            <input type="submit" className="button-primary u-full-width" value="Agregar gasto"/>
        </form>
     );
}
 
export default Formulario;
